package com.xworkz.patterns.starPattern;

public class TriangleRight {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=4;
		for(int i=0;i<=num;i++)
		{
			for(int j=0;j<i+1;j++)
			{
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
